INSERT INTO public.furniture (id, description, name, price, priority, type, furniture_type_of_order_id) VALUES (4, 'Перетяжка кутового п-образного дивана. Тканини шинил, у виробі зроблено заміну паралону, виконано ремонт столярні + заміна механізму трансформації. У вартість матеріалів увійде тканина при необхідності паралон, синтапон технічні тканини клей скоба при необхідності механізм трансформації.', 'Кутовий диван', '7500 грн + матеріали', 'LOW', null, 5);
